CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_USER_FREQ_MENU_SCH`()
ERR_TRAN: BEGIN








  
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_USER_FREQ_MENU_SCH',@P1,@P2);
	END;
     
  
  START TRANSACTION;
  

  
  DELETE FROM CM_DB.USER_FREQ_MENU WHERE USER_REG_YN = 'N';
  
  











      
  SET @RANK = 0;
  INSERT INTO CM_DB.USER_FREQ_MENU (MENU_ORDER, MENU_ID, USER_ID, USER_REG_YN)
  SELECT R.RANK_tmp + 1, R.MENU_ID, R.USER_ID, 'N'
  FROM 
  (
    SELECT T.USER_ID, T.MENU_ID, 
    CASE WHEN @USER_ID = T.USER_ID THEN @RANK := @RANK + 1 
    ELSE @RANK := 0
    END RANK_tmp, @USER_ID := T.USER_ID, T.CNT
    FROM
    (
      SELECT USER_ID, MENU_ID, COUNT(MENU_ID) AS CNT
      FROM CM_DB.USER_FREQ_MENU_HIST
      WHERE REG_DT > DATE_ADD(NOW(), INTERVAL -10 DAY)
      GROUP BY USER_ID, MENU_ID
    ) T 
    ORDER BY T.USER_ID, T.CNT DESC
  ) R
  WHERE R.RANK_tmp < 10;
  
  
  COMMIT;
  
END ;;
