CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_YUANTA_INVEN_OPEN_ELAPSE_MONTH_MULTI`(
    IN _INDICATOR_ID_LIST VARCHAR(255)
    ,IN _INDICATOR_PASSMONTH_ID INT
    ,IN _INDICATOR_OPENDT_ID INT
)
BEGIN
    DECLARE _COLUMN_ID VARCHAR(100);
    DECLARE _FIELD_NM VARCHAR(50);
    DECLARE _DATA_TYPE_NM VARCHAR(50);
    DECLARE _INDICATOR_ID INT;
    DECLARE done INT DEFAULT FALSE;


    DECLARE CUR1 CURSOR FOR 
    SELECT 
        CONCAT('INDICATOR_ID_',INDICATOR_ID) AS COLUMN_ID
        ,INDICATOR_NM AS FIELD_NM
        ,FC_CM_GET_CD_NM(2132, IFNULL(DATA_TYPE_CD, 'ST'))  AS DATA_TYPE_NM
        ,INDICATOR_ID
    FROM
        CSF_INDICATOR
    WHERE
        DEST_GB = 'M'
        AND FIND_IN_SET(INDICATOR_ID, _INDICATOR_ID_LIST)
    ;
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
    
    SET @STR_COL_LIST = "";
    SET @STR_FROM_LIST = "";
    
    
    
    OPEN CUR1;   
    LOOP1: LOOP 
    FETCH CUR1 
    INTO _COLUMN_ID
        , _FIELD_NM
        ,_DATA_TYPE_NM
        ,_INDICATOR_ID
        ;
        
        IF done THEN
            LEAVE LOOP1;
        END IF;
        
        SET @STR_COL_LIST = CONCAT(@STR_COL_LIST,",I.INDICATOR_ID_",CONCAT(_INDICATOR_ID));
        SET @STR_FROM_LIST = CONCAT(@STR_FROM_LIST,",MAX(CASE WHEN INDICATOR_ID = ",CONCAT(_INDICATOR_ID)," THEN VAL_TEXT ELSE NULL END) AS INDICATOR_ID_",CONCAT(_INDICATOR_ID));
        
    END LOOP;
        
    CLOSE CUR1;
    
    
    
    BEGIN
    
    
    
    DROP VIEW IF EXISTS V_INVEN_YUANTA_INFO;
    SET @str = CONCAT(    
    "CREATE VIEW V_INVEN_YUANTA_INFO AS "
    ,"SELECT "
    ,"	T.* "
    ,"FROM "
    ,"	( "
    ,"	SELECT "
    ,"		'Y' AS MANUAL_REG_YN"
    ,"		, A.INVEN_MCH_ID AS INVEN_ID"
    ,@STR_COL_LIST
    ,"        ,VMI.MCH_GB_CD"
    ,"	FROM "
    ,"		INVEN_MCH_INFO A "
    ,"        LEFT OUTER JOIN V_MCH_INFO VMI ON A.MCH_ID = VMI.MCH_ID"
    ,"        LEFT OUTER JOIN ("
	,"			SELECT "
	,"				OBJ_ID "
	,"				, GROUP_ID "
	,"			FROM "
	,"				GROUP_OBJ_LIST "
	,"			WHERE "
	,"				GROUP_ID IN (SELECT GROUP_ID FROM GROUP_INFO WHERE GROUP_PATH_YN='Y' AND GROUP_CD=1) "
	,"			GROUP BY OBJ_ID"
	,"		) L1 ON A.MCH_ID = L1.OBJ_ID "
	,"		LEFT OUTER JOIN ("
	,"			SELECT "
	,"				GROUP_ID"
	,"				,GROUP_NM"
	,"				,GROUP_PATH "
	,"			FROM "
	,"				GROUP_INFO "
	,"			WHERE "
	,"				GROUP_PATH_YN='Y' "
	,"		)G1 ON L1.GROUP_ID=G1.GROUP_ID"
    ,"		LEFT OUTER JOIN ("
    ,"			SELECT "
    ,"				INVEN_ID AS DEST_ID "
    ,@STR_FROM_LIST
    ,"			FROM "
    ,"				INVEN_CSF_DATA GROUP BY INVEN_ID "
    ,"		) I ON I.DEST_ID=A.INVEN_MCH_ID "
    ,"	WHERE "
    ,"		A.INVEN_DEL_YN='N' "
    ,"		AND (A.MCH_DEL_YN='Y' OR A.MCH_ID=0) "
    ,"	UNION ALL "
    ,"		SELECT "
    ,"			'N' AS MANUAL_REG_YN"
    ,"			, A.MCH_ID AS INVEN_ID"
    ,@STR_COL_LIST
    ,"          ,A.MCH_GB_CD"
    ,"		FROM "
    ,"			V_MCH_INFO A"
    ,"            INNER JOIN MCH_COLLECT_POLICY M ON A.MCH_ID = M.MCH_ID "
	,"			INNER JOIN INVEN_MCH_INFO IM ON A.MCH_ID=IM.MCH_ID "
	,"			LEFT OUTER JOIN MCH_UI_INFO B ON A.MCH_ID = B.MCH_ID "
	,"			LEFT OUTER JOIN MCH_ADD_INFO C ON A.MCH_ID = C.MCH_ID "
	,"			LEFT OUTER JOIN MCH_INFO D ON A.MCH_ID=D.MCH_ID "
	,"			LEFT OUTER JOIN ("
	,"				SELECT "
	,"					OBJ_ID "
	,"					, GROUP_ID "
	,"				FROM "
	,"					GROUP_OBJ_LIST "
	,"				WHERE "
	,"					GROUP_ID IN ("
	,"						SELECT "
	,"							GROUP_ID "
	,"						FROM "
	,"							GROUP_INFO "
	,"						WHERE "
	,"							GROUP_PATH_YN='Y' "
	,"							AND GROUP_CD=1"
	,"					) GROUP BY OBJ_ID"
	,"			) L1 ON A.MCH_ID = L1.OBJ_ID "
	,"			LEFT OUTER JOIN ("
	,"				SELECT "
	,"					GROUP_ID"
	,"					,GROUP_NM"
	,"					,GROUP_PATH "
	,"				FROM "
	,"					GROUP_INFO "
	,"				WHERE "
	,"					GROUP_PATH_YN='Y' "
	,"			)G1 ON L1.GROUP_ID=G1.GROUP_ID "
    ,"			LEFT OUTER JOIN ("
    ,"				SELECT "
    ,"					DEST_ID "
    ,@STR_FROM_LIST
    ,"				FROM "
    ,"					CSF_DATA "
    ,"              WHERE "
    ,"                DEST_GB='M' "
    ,"              GROUP BY DEST_ID "
    ,"			) I ON I.DEST_ID=A.MCH_ID"
    ,"	) T"
    ," WHERE"
    ,"    INDICATOR_ID_",_INDICATOR_OPENDT_ID," IS NOT NULL AND INDICATOR_ID_",_INDICATOR_OPENDT_ID," != '' "
    );
    PREPARE stmt FROM @str;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;
    
    
        BEGIN
    
        DECLARE _INVEN_ID INT;
        DECLARE _MANUAL_REG_YN CHAR(1);
        DECLARE _OPENDT DATETIME;
        DECLARE _PASSMONTH VARCHAR(256);
        DECLARE _STOPYN VARCHAR(256);
        DECLARE _MCH_GB_CD VARCHAR(6);
        DECLARE done2 INT DEFAULT FALSE;
        DECLARE CUR2 CURSOR FOR 
            SELECT 
                *
            FROM
                V_INVEN_YUANTA_INFO
        ;
        
        DECLARE CONTINUE HANDLER FOR NOT FOUND SET done2 = TRUE;
        
        
        OPEN CUR2;
        
        LOOP2: LOOP
        
        FETCH CUR2 INTO 
            _MANUAL_REG_YN
            ,_INVEN_ID
            , _OPENDT
            , _PASSMONTH
            , _STOPYN
            , _MCH_GB_CD;
            
        IF done2 THEN
            LEAVE LOOP2;
        END IF; 
            CALL SP_NMS_YUANTA_INVEN_OPEN_ELAPSE_MONTH_SINGLE(_INVEN_ID,_MANUAL_REG_YN,'M',_INDICATOR_PASSMONTH_ID,STR_TO_DATE(_OPENDT, '%Y-%m-%d %H:%i:%s'));
               
        END LOOP LOOP2;
        CLOSE CUR2;
        
        
        DROP VIEW V_INVEN_YUANTA_INFO;
        
        END;
    
    END;
    
END ;;
