CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_YUANTA_INVEN_OPEN_ELAPSE_MONTH_SINGLE`(
IN _INVEN_ID INT
,IN _MANUAL_REG_YN CHAR(1)
,IN _DEST_GB CHAR(1)
,IN _INDICATOR_ID INT
,IN _OPEN_DT DATETIME
)
BEGIN
    
    
    
    
    

    DECLARE done INT DEFAULT FALSE;
    DECLARE _REG_DT DATETIME;
    DECLARE _STOP_YN CHAR(1);
    DECLARE V_COUNT INT DEFAULT 0;
    DECLARE _START_DT DATETIME;
    DECLARE _END_DT DATETIME DEFAULT NOW();
    
    SET @MONTH_COUNT = 0;
    SET @TOGGLE_YN = '';
    SET @LIST_COUNT = 0;
    
    
    SELECT
        COUNT(*) INTO @LIST_COUNT
    FROM
        INVEN_MCH_STOP_HIST
    WHERE
        INVEN_ID = _INVEN_ID
    ;
    
    
    IF @LIST_COUNT = 0 THEN
        SET @MONTH_COUNT = CAST(TIMESTAMPDIFF(DAY, _OPEN_DT, NOW()) AS UNSIGNED);
    ELSE
   
       
        BEGIN
        
        DECLARE CUR1 CURSOR FOR 
        SELECT 
            REG_DT
            ,STOP_YN
        FROM 
            INVEN_MCH_STOP_HIST
        WHERE
            INVEN_ID = _INVEN_ID
        ORDER BY REG_DT ASC
        ;

        DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

        OPEN CUR1;
        
        my_loop: LOOP
     
        
        FETCH CUR1 
        INTO _REG_DT
            , _STOP_YN;
        
        
        
        
        IF done THEN
            LEAVE my_loop;
        END IF;
        
        SET V_COUNT = V_COUNT +1 ;
        
        
        IF (@TOGGLE_YN != _STOP_YN) THEN
            SET @TOGGLE_YN = _STOP_YN;
            
            IF (_STOP_YN = 'Y') THEN
                IF (V_COUNT = 1) THEN
                    SET _START_DT = _OPEN_DT;                
                END IF;
                SET _END_DT =_REG_DT;
                
                SET @MONTH_COUNT = @MONTH_COUNT + CAST(TIMESTAMPDIFF(DAY , _START_DT, _END_DT) AS UNSIGNED);
                
            ELSEIF (_STOP_YN = 'N') THEN
                
                SELECT V_COUNT;
                IF (V_COUNT = 1) THEN
                    SET _START_DT = _OPEN_DT;
                ELSE
                    SET _START_DT = _REG_DT;
                END IF;
                IF (@LIST_COUNT = V_COUNT) THEN
                    SET _END_DT = NOW();
                    SET @MONTH_COUNT = @MONTH_COUNT + CAST(TIMESTAMPDIFF(DAY , _START_DT, _END_DT)AS UNSIGNED);
                END IF;
                SELECT _START_DT;
                
            END IF;
        END IF;
        
        
        
            
        END LOOP;
        
     
        
        CLOSE CUR1;
      
        END;
        
    END IF;    
    
    
        
    

    IF (_MANUAL_REG_YN = 'Y') THEN
        INSERT INTO INVEN_CSF_DATA(
            INVEN_ID
            ,DEST_GB
            ,INDICATOR_ID
            ,VAL_TEXT
        )VALUES(
            _INVEN_ID
            ,_DEST_GB
            ,_INDICATOR_ID
            ,IFNULL(@MONTH_COUNT,0) DIV 30
        )
        ON DUPLICATE KEY UPDATE
        VAL_TEXT = IFNULL(@MONTH_COUNT,0) DIV 30;
    
    ELSEIF (_MANUAL_REG_YN = 'N') THEN
        INSERT INTO CSF_DATA(
            DEST_ID
            ,DEST_GB
            ,INDICATOR_ID
            ,VAL_TEXT
        )VALUES(
            _INVEN_ID
            ,_DEST_GB
            ,_INDICATOR_ID
            ,IFNULL(@MONTH_COUNT,0) DIV 30
        )
        ON DUPLICATE KEY UPDATE
        VAL_TEXT = IFNULL(@MONTH_COUNT,0) DIV 30;
    END IF;
    
    COMMIT;
    
END ;;
