CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_REPORT_EVENT`(
IN _SDATE DATETIME,
IN _EDATE DATETIME,
IN _EVENT_TYPE CHAR(30),
IN _USER_ID CHAR(30)
)
ERR_TRAN: BEGIN










DECLARE __WHERE_LIMIT_CNT CHAR(100);
DECLARE __LIMIT_CNT INT;

  
DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_REPORT_EVENT',@P1,@P2);
	END;

SET __WHERE_LIMIT_CNT = "";
SET __LIMIT_CNT = 10; 

IF __LIMIT_CNT > 0 THEN
  SET __WHERE_LIMIT_CNT = CONCAT(" LIMIT ", __LIMIT_CNT);
END IF;

SET @USER_ID = _USER_ID;
SET @VAR = '';


  START TRANSACTION;
  IF _EVENT_TYPE = 'EVENT_GRADE_LIST' THEN
    SET @VAR = CONCAT(
    "SELECT "
  ,     "MAX(CASE WHEN EVENT_LEVEL = 1 THEN CNT	ELSE 0 END) AS EV1 "
	,   ", MAX(CASE WHEN EVENT_LEVEL = 2 THEN CNT	ELSE 0 END) AS EV2 "
  ,   ", MAX(CASE WHEN EVENT_LEVEL = 3 THEN CNT ELSE 0 END) AS EV3 "
  ,   ", MAX(CASE WHEN EVENT_LEVEL = 4 THEN CNT ELSE 0 END) AS EV4 "
  ,   ", MAX(CASE WHEN EVENT_LEVEL = 5 THEN CNT	ELSE 0 END) AS EV5 "
	, "FROM (SELECT EVENT_LEVEL, COUNT(EVENT_LEVEL) AS CNT "
  ,       "FROM (SELECT E.EVENT_ID, MIN(E.EVENT_LEVEL) AS EVENT_LEVEL "
  ,             "FROM EVENT_HIST E LEFT OUTER JOIN V_PRIV_S_MCH_INFO M ON E.MCH_ID = M.MCH_ID "
  ,             "WHERE E.OCCUR_DT BETWEEN '", _SDATE, "' AND '", _EDATE, "' "
  ,               "AND (E.MCH_ID = 0 OR M.MCH_ID > 0) "
  ,               "AND E.EVENT_LEVEL > 0 "
  ,             "GROUP  BY E.EVENT_ID "
  ,      ") T "
	, "GROUP BY EVENT_LEVEL "
  , ") T");

  ELSEIF _EVENT_TYPE = 'EVENT_GRAPH_LIST' THEN
    SET @VAR = CONCAT(
    "SELECT "
  ,     "DT, MIN(OCCUR_DT) AS OCCUR_DT "
  ,   ", COUNT(CASE WHEN EVENT_LEVEL = 1 THEN 1 ELSE NULL	END) AS EV1 "
  ,   ", COUNT(CASE WHEN EVENT_LEVEL = 2 THEN 1 ELSE NULL END) AS EV2 "
  ,   ", COUNT(CASE WHEN EVENT_LEVEL = 3 THEN 1	ELSE NULL END) AS EV3 "
  ,   ", COUNT(CASE WHEN EVENT_LEVEL = 4 THEN 1 ELSE NULL	END) AS EV4 "
	,   ", COUNT(CASE WHEN EVENT_LEVEL = 5 THEN 1	ELSE NULL END) AS EV5 "
	, "FROM (SELECT E.OCCUR_DT, DATE_FORMAT(E.OCCUR_DT, '%Y-%m-%e %H:00:00') AS DT, E.EVENT_LEVEL "
  ,       "FROM EVENT_HIST E LEFT OUTER JOIN V_PRIV_S_MCH_INFO M ON E.MCH_ID = M.MCH_ID "
  ,       "WHERE OCCUR_DT BETWEEN '", _SDATE, "' AND '", _EDATE, "' "
  ,         "AND (E.MCH_ID = 0 OR M.MCH_ID > 0) "
  ,         "AND E.EVENT_LEVEL > 0 "
  ,      ") T "
  , "GROUP BY DT");

  ELSEIF _EVENT_TYPE = 'EVENT_GRADE_TYPE_LIST' THEN
    SET @VAR = CONCAT(
    "SELECT CD_NM, EV1, EV2, EV3, EV4, EV5, (EV1 + EV2 + EV3 + EV4 + EV5) AS EVSUM "
  , "FROM (SELECT "
  ,           "CD_NM "
  ,         ", COUNT(CASE WHEN EVENT_LEVEL = 1 THEN 1 ELSE NULL END) AS EV1 "
  ,         ", COUNT(CASE WHEN EVENT_LEVEL = 2 THEN 1	ELSE NULL	END) AS EV2 "
  ,         ", COUNT(CASE WHEN EVENT_LEVEL = 3 THEN 1 ELSE NULL	END) AS EV3 "
  ,         ", COUNT(CASE WHEN EVENT_LEVEL = 4 THEN 1 ELSE NULL	END) AS EV4 "
  ,         ", COUNT(CASE WHEN EVENT_LEVEL = 5 THEN 1 ELSE NULL	END) AS EV5 "
  ,      "FROM (SELECT E.EVENT_CD, MIN(E.EVENT_LEVEL) AS EVENT_LEVEL "
  ,            "FROM EVENT_HIST E LEFT OUTER JOIN V_PRIV_S_MCH_INFO M ON E.MCH_ID = M.MCH_ID "
  ,            "WHERE E.OCCUR_DT BETWEEN '", _SDATE, "' AND '", _EDATE, "' "
  ,              "AND E.EVENT_LEVEL > 0 "
  ,              "AND (E.MCH_ID = 0 OR M.MCH_ID > 0) "
  ,            "GROUP BY E.EVENT_ID, E.EVENT_CD "
  ,            ") A JOIN EVENT_CD_INFO B ON A.EVENT_CD = B.CD_ID "
	,      "GROUP BY CD_NM "
  ,     ") T "
  , "ORDER BY EV1 DESC, EV2 DESC, EV3 DESC, EV4 DESC, EV5 DESC "
  ,	__WHERE_LIMIT_CNT);

  ELSEIF _EVENT_TYPE = 'EVENT_DEVICE_LIST_TOPN' THEN
    SET @VAR = CONCAT(
		"SELECT IFNULL(MCH_NM, CONCAT('Unknown_', A.MCH_ID)) AS MCH_NM, A.MCH_IP, A.MCH_ID, EV1, EV2, EV3, EV4, EV5 "
  , "FROM (SELECT "
  ,           "MCH_ID,MCH_IP "
  ,         " ,COUNT(CASE WHEN EVENT_LEVEL = 1 THEN 1 ELSE NULL	END) AS EV1 "
  ,         " ,COUNT(CASE WHEN EVENT_LEVEL = 2 THEN 1	ELSE NULL	END) AS EV2 "
  ,         " ,COUNT(CASE WHEN EVENT_LEVEL = 3 THEN 1 ELSE NULL	END) AS EV3 "
  ,         " ,COUNT(CASE WHEN EVENT_LEVEL = 4 THEN 1	ELSE NULL	END) AS EV4 "
  ,         " ,COUNT(CASE WHEN EVENT_LEVEL = 5 THEN 1	ELSE NULL	END) AS EV5 "
	,      "FROM (SELECT E.MCH_ID, E.MCH_IP, MIN(E.EVENT_LEVEL) AS EVENT_LEVEL "
  ,            "FROM EVENT_HIST E LEFT OUTER JOIN V_PRIV_S_MCH_INFO M ON E.MCH_ID = M.MCH_ID "
  ,            "WHERE E.OCCUR_DT BETWEEN '", _SDATE, "' AND '", _EDATE, "' "
  ,              "AND E.EVENT_LEVEL > 0 "
  ,              "AND (E.MCH_ID = 0 OR M.MCH_ID > 0) "
  ,            "GROUP BY E.EVENT_ID, E.MCH_ID "
	,           ") T "
	, "GROUP BY MCH_ID, MCH_IP "
	, "ORDER BY EV1 DESC, EV2 DESC, EV3 DESC, EV4 DESC, EV5 DESC "
	, "LIMIT 10 "
  , ") A LEFT OUTER JOIN V_MCH_INFO B ON A.MCH_ID = B.MCH_ID ");

  ELSEIF _EVENT_TYPE = 'EVENT_DEVICE_TYPE_LIST' THEN
    SET @VAR = CONCAT(
    "SELECT "
  ,     "B.CD_NM, IFNULL(C.MCH_NM,CONCAT('Unknown_', A.MCH_ID)) AS MCH_NM, A.MCH_IP "
  ,   " ,FC_CM_GET_CD_NM('2000', A.DEST_CD) AS DEST_TYPE_NM, A.DEST_NM "
  ,   " ,A.IF_ALIAS, A.EVENT_LEVEL "
	, "FROM ( "
  ,       "SELECT MCH_ID, MCH_IP, DEST_CD, DEST_NM, IF_ALIAS, EVENT_CD, COUNT(EVENT_LEVEL) AS EVENT_LEVEL "
	,       "FROM ( "
  ,             "SELECT MCH_ID, MCH_IP, DEST_CD, DEST_NM, IF_ALIAS, EVENT_CD, MIN(EVENT_LEVEL) AS EVENT_LEVEL "
  ,             "FROM EVENT_HIST "
  ,             "WHERE OCCUR_DT BETWEEN '", _SDATE, "' AND '", _EDATE, "' "
  ,               "AND EVENT_LEVEL > 0 "
	,  					  "GROUP BY EVENT_ID, MCH_ID, MCH_IP, DEST_CD, DEST_NM, IF_ALIAS, EVENT_CD "
  ,            ") T "
  ,       "GROUP BY MCH_ID, MCH_IP, DEST_CD, DEST_NM, IF_ALIAS, EVENT_CD "
	,      ") A JOIN EVENT_CD_INFO B ON A.EVENT_CD = B.CD_ID "
  ,          "LEFT OUTER JOIN V_PRIV_T_MCH_INFO C ON A.MCH_ID = C.MCH_ID "
	, "WHERE (A.MCH_ID = 0 OR C.MCH_ID > 0) "
	, "ORDER BY EVENT_LEVEL DESC "
  ,	__WHERE_LIMIT_CNT);
  END IF;
      
  
  
  PREPARE stmt FROM @VAR;
   EXECUTE stmt;
END ;;
