FETCH CUR1 INTO _topn_cnt, _INDIC_TYPE, _device_type_cd;
    
    IF NOT done THEN
    START TRANSACTION;
      IF _device_type_cd = '1' THEN 
        IF _INDIC_TYPE = 'CPU' THEN
          SET @VAR = CONCAT(@VAR, " UNION ALL ",
      CONCAT("(SELECT "
          ,     " DATE_FORMAT(PERF.REG_DT, '%Y-%m-%e %H:00:00') AS REG_DT "
