          ,    ", '", _device_type_cd, "' AS INDIC_NUM "
          ,    ", '", _INDIC_TYPE, "' AS INDIC_TYPE "
          ,    ", CASE WHEN '", _INDIC_TYPE, "' = 'CLIENT_CNT' THEN '104' "
