          ,    ", CAST(PERF.", _INDIC_TYPE, "_MAX AS DECIMAL(12,2)) AS PERF_MAX, CAST(PERF.", _INDIC_TYPE, "_MXA AS DECIMAL(12,2)) AS PERF_MXA "
          ,  "FROM ("
