          , "ORDER BY PERF.REG_DT DESC "
          , "LIMIT "
          , _TOPN_CNT, ") "));
  END IF;  
      END IF;
    COMMIT;
    END IF;
  
UNTIL done END REPEAT;
    
CLOSE CUR1;
  SET @VAR = CONCAT(SUBSTRING(@VAR, 11), " ORDER BY INDIC_NUM, INDIC_TYPE_CD, DATE(REG_DT) ");
  
  
   PREPARE stmt FROM @VAR;
   EXECUTE stmt;
  DEALLOCATE PREPARE stmt;
END;
