DECLARE __HLDY_EX_YN char(30);

DECLARE done int DEFAULT FALSE;
DECLARE _topn_cnt int;
DECLARE _INDIC_TYPE char(20);
DECLARE _FLOW CHAR(3);
DECLARE _NO int;
DECLARE _COLNAME CHAR(3);
   
  DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;
    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
