                                  c.TOPN_CNT  AS TOPN_CNT, 
                                  c.INDIC_NM  AS INDIC_NM, 
                                  d.FLOW      AS FLOW,
                                  d.COLNAME   AS COLNAME
                          FROM RPT_CHOICE c
                          INNER JOIN ( 
                            SELECT 'BPS' AS INDIC_NM,     'IN'  AS FLOW,  'PC' AS COLNAME,      1 AS no UNION ALL
                            SELECT 'BPS' AS INDIC_NM,     'OUT' AS FLOW,  'PC' AS COLNAME,      2 AS no UNION ALL
                            SELECT 'PPS' AS INDIC_NM,     'IN'  AS FLOW,  'DC' AS COLNAME,      3 AS no UNION ALL
                            SELECT 'PPS' AS INDIC_NM,     'OUT' AS FLOW,  'DC' AS COLNAME,      4 AS no UNION ALL
                            SELECT 'ERROR' AS INDIC_NM,   'IN' AS FLOW,   'DC' AS COLNAME,      5 AS no UNION ALL
                            SELECT 'ERROR' AS INDIC_NM,   'OUT' AS FLOW,  'DC' AS COLNAME,      6 AS no UNION ALL
                            SELECT 'DISCARD' AS INDIC_NM, 'IN' AS FLOW,   'DC' AS COLNAME,      7 AS no UNION ALL
                            SELECT 'DISCARD' AS INDIC_NM, 'OUT' AS FLOW,  'DC' AS COLNAME,      8 AS no UNION ALL
                            SELECT 'U_PPS' AS INDIC_NM,   'IN' AS FLOW,   'DC' AS COLNAME,      9 AS no UNION ALL     
                            SELECT 'U_PPS' AS INDIC_NM,   'OUT' AS FLOW,  'DC' AS COLNAME,      10 AS no UNION ALL   
                            SELECT 'M_PPS' AS INDIC_NM,   'IN' AS FLOW,   'DC' AS COLNAME,      11 AS no UNION ALL      
                            SELECT 'M_PPS' AS INDIC_NM,   'OUT' AS FLOW,  'DC' AS COLNAME,      12 AS no UNION ALL     
                            SELECT 'B_PPS' AS INDIC_NM,   'IN' AS FLOW,   'DC' AS COLNAME,      13 AS no UNION ALL      
                            SELECT 'B_PPS' AS INDIC_NM,   'OUT' AS FLOW,  'DC' AS COLNAME,      14 AS no UNION ALL     
                            SELECT 'CRC' AS INDIC_NM,     'ONE' AS FLOW,  'DC' AS COLNAME,      15 AS no UNION ALL
                            SELECT 'COLLISION' AS INDIC_NM, 'ONE' AS FLOW,'DC' AS COLNAME,      16 AS no
                          ) AS d 
