REPEAT

FETCH CUR1 INTO _NO,_topn_cnt,_INDIC_TYPE, _FLOW, _COLNAME;

  IF NOT done THEN
  START TRANSACTION;
    IF _INDIC_TYPE IN ('BPS', 'PPS', 'ERROR', 'DISCARD', 'U_PPS', 'M_PPS', 'B_PPS') THEN

        SET @VAR = 
          CONCAT(@VAR, " UNION ALL ", 
