            , "               CAST(P.",_INDIC_TYPE,"_IN_",_COLNAME,"_MXA AS DECIMAL(12,2)) AS IN_MXA, "
            , "               CAST(P.",_INDIC_TYPE,"_OUT_",_COLNAME,"_MAX AS DECIMAL(12,2)) AS OUT_MAX, "
            , "               CAST(P.",_INDIC_TYPE,"_OUT_",_COLNAME,"_MXA AS DECIMAL(12,2)) AS OUT_MXA, "
            , "               NULL AS ONE_MAX, NULL AS ONE_MXA "
            , "       FROM   PERF_H_IF P "
            , "       INNER JOIN ( "
            , "         SELECT  REG_DT, IF_ID, MCH_ID, "

            , "                 ","MAX(",_INDIC_TYPE,"_IN_",_COLNAME,"_MAX","), " 
            , "                 ","MAX(",_INDIC_TYPE,"_IN_",_COLNAME,"_MXA","), " 
            , "                 ","MAX(",_INDIC_TYPE,"_OUT_",_COLNAME,"_MAX","), " 
            , "                 ","MAX(",_INDIC_TYPE,"_OUT_",_COLNAME,"_MXA",") " 
            , "         FROM PERF_H_IF "

            , "         WHERE REG_DT BETWEEN '",_SDATE,"' AND '",_EDATE,"' " 
            , __BHOUR_YN, __HLDY_EX_YN
            , "         GROUP BY IF_ID "
            , "         ORDER BY ","MAX(", _INDIC_TYPE,"_",_FLOW,"_",_COLNAME,"_MAX",") DESC, IF_ID ASC "  
            , "         LIMIT ", _topn_cnt
            , "       ) R "
            , "       	ON P.IF_ID = R.IF_ID "
			      , "		  INNER JOIN V_PRIV_T_IF_INFO I "
			      , " 		ON I.IF_ID = R.IF_ID "

            , "       WHERE  P.REG_DT BETWEEN '",_SDATE,"' AND '",_EDATE,"' "  
            , ") "
            )
          );

    ELSEIF _INDIC_TYPE IN ('CRC', 'COLLISION') THEN
        SET @VAR = 
          CONCAT(@VAR, " UNION ALL ", 
            CONCAT(" " 
            , "( "
            , "       SELECT  P.REG_DT, "
            , "               '", _INDIC_TYPE,"_" ,_FLOW,"' AS INDIC_TYPE_FLOW, "
            , "               R.IF_ID, I.MCH_ID, I.MCH_NM, I.MCH_IP, I.IF_NM, "
            , "               I.UI_ALIASSPEED, I.UI_ALIAS, "
            , "               NULL AS IN_MAX, "
            , "               NULL AS IN_MXA, "
            , "               NULL AS OUT_MAX, "
            , "               NULL AS OUT_MXA, "
            , "               CAST(P.",_INDIC_TYPE,"_",_COLNAME,"_MAX AS DECIMAL(12,2)) AS ONE_MAX, "
            , "               CAST(P.",_INDIC_TYPE,"_",_COLNAME,"_MXA AS DECIMAL(12,2)) AS ONE_MXA "
            , "       FROM   PERF_H_IF P "
            , "       INNER JOIN ( "
            , "         SELECT  REG_DT, IF_ID, MCH_ID, "

            , "                 ","MAX(",_INDIC_TYPE,"_",_COLNAME,"_MAX",") AS ONE_MAX, " 
            , "                 ","MAX(",_INDIC_TYPE,"_",_COLNAME,"_MXA",") AS ONE_MXA " 


            , "         FROM PERF_H_IF "

            , "         WHERE REG_DT BETWEEN '",_SDATE,"' AND '",_EDATE,"' " 
            , __BHOUR_YN, __HLDY_EX_YN
            , "         GROUP BY IF_ID "
            , "         ORDER BY ","MAX(", _INDIC_TYPE,"_",_COLNAME,"_MAX",") DESC, IF_ID ASC "  
            , "         LIMIT ", _topn_cnt
            , "       ) R "
            , "       	ON P.IF_ID = R.IF_ID "
			      , "		  INNER JOIN V_PRIV_T_IF_INFO I "
			      , " 		ON I.IF_ID = R.IF_ID "

            , "       WHERE  P.REG_DT BETWEEN '",_SDATE,"' AND '",_EDATE,"' "  
            , ") "
            )
          );
    END IF;    
  END IF;

UNTIL done END REPEAT;

CLOSE CUR1;
  SET @VAR = SUBSTRING(@VAR, 11);

  PREPARE stmt FROM @VAR;
  EXECUTE stmt;
  DEALLOCATE PREPARE stmt;
END;
