CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_TEST_MANUAL_MCH_SET`(
  IN _RUN_NO INT,
  IN _BASE_IP VARCHAR(50))
BEGIN
      
  DECLARE v_finish INT DEFAULT 0;
  DECLARE V_MCH_ID INT DEFAULT 0;
  DECLARE V_MCH_NM VARCHAR(256);
  DECLARE V_MCH_IP VARCHAR(50);
  DECLARE N_MCH_ID INT DEFAULT 0;
    
  DECLARE CUR_JOB CURSOR FOR 
  SELECT MCH_ID, MCH_NM FROM TMP_BASE_MCH_INFO;
      
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_finish = 1;
  
  OPEN CUR_JOB;
  
  LOOP_JOB : LOOP
  
    FETCH CUR_JOB INTO V_MCH_ID, V_MCH_NM;
    
    IF v_finish = 1 THEN
      LEAVE LOOP_JOB;
    END IF;
        
    SET V_MCH_NM = CONCAT(V_MCH_NM, '(',_RUN_NO, ')');
    SET V_MCH_IP = CONCAT(_BASE_IP, '.', V_MCH_ID);
    
    INSERT INTO TMP_MCH_INFO
    (R_MCH_ID,MCH_IP, MCH_NM, MODEL_ID, 
        SYS_OBJECTID, SYS_NAME, SYS_DESC, SNMP_TMPL_ID, TERMINAL_TMPL_ID,
        SYS_UPTIME, SYS_LOCATION, SYS_CONTACT, WEB_LINK, SERIAL_NO,
        ICMP_YN, SNMP_YN, TERMINAL_YN, TERMINAL_GB, TERMINAL_PORT, RC_STATE_YN, SNMP3_ENG_ID,
        IMG_CD,  MCH_TYPE_CD)
    SELECT V_MCH_ID,V_MCH_IP, V_MCH_NM, MODEL_ID, 
        SYS_OBJECTID, SYS_NAME, SYS_DESC, SNMP_TMPL_ID, TERMINAL_TMPL_ID,
        SYS_UPTIME, SYS_LOCATION, SYS_CONTACT, WEB_LINK, SERIAL_NO,
        ICMP_YN, SNMP_YN, TERMINAL_YN, TERMINAL_GB, TERMINAL_PORT, RC_STATE_YN, SNMP3_ENG_ID,
        IMG_CD,  MCH_TYPE_CD
    FROM TMP_BASE_MCH_INFO 
    WHERE MCH_ID = V_MCH_ID;
    
    SELECT LAST_INSERT_ID() INTO N_MCH_ID;
    
    INSERT INTO NMS_DB.TMP_MCH_IF_INFO(
      R_IF_ID,MCH_ID,IF_INDEX,IF_NM,IF_NM_SORT,IF_DESC,IF_ALIAS,IF_MAXSPEED,IF_ALIASSPEED,
      IF_TYPE,IF_MODE,IF_MTU,UI_ALIAS,UI_MAXSPEED,UI_ALIASSPEED,INDEX_GB,
      IF_ADMIN_STATE,IF_OPER_STATE,FLOW_COLLECT_YN,
      OID_BPS_IN,OID_BPS_OUT,OID_PPS_IN,OID_PPS_OUT,OID_DISCARD_IN,OID_DISCARD_OUT,
      OID_ERROR_IN,OID_ERROR_OUT,OID_COLLISION,OID_CRC )
    SELECT I.IF_ID,N_MCH_ID,I.IF_INDEX,I.IF_NM,I.IF_NM_SORT,I.IF_DESC,I.IF_ALIAS,I.IF_MAXSPEED,I.IF_ALIASSPEED,
      I.IF_TYPE,I.IF_MODE,I.IF_MTU,I.UI_ALIAS,I.UI_MAXSPEED,I.UI_ALIASSPEED,I.INDEX_GB,
      S.IF_ADMIN_STATE,S.IF_OPER_STATE,S.FLOW_COLLECT_YN,
      IM.OID_BPS_IN,IM.OID_BPS_OUT,IM.OID_PPS_IN,IM.OID_PPS_OUT,IM.OID_DISCARD_IN,IM.OID_DISCARD_OUT,
      IM.OID_ERROR_IN,IM.OID_ERROR_OUT,IM.OID_COLLISION,IM.OID_CRC
    FROM MCH_IF_INFO I
    JOIN MCH_IF_MIB IM ON I.IF_ID = IM.IF_ID
    JOIN MCH_IF_STATE S ON I.IF_ID = S.IF_ID
    WHERE I.MCH_ID = V_MCH_ID;
    
    INSERT INTO NMS_DB.TMP_MCH_CPU(MCH_ID,R_CPU_ID,CPU_GB,CPU_NO,OID_CPU)
    SELECT N_MCH_ID,CPU_ID,CPU_GB,CPU_NO,OID_CPU 
    FROM MCH_CPU
    WHERE MCH_ID = V_MCH_ID;
        
    INSERT INTO NMS_DB.TMP_MCH_MEMORY(MCH_ID, R_MEMORY_ID, MEMORY_GB, MEMORY_NM, OID_MEMORY, OID_INDEX, MEMORY_CALC, DEL_YN) 
    SELECT N_MCH_ID,MEMORY_ID,MEMORY_GB, MEMORY_NM, OID_MEMORY, OID_INDEX, MEMORY_CALC, DEL_YN
    FROM MCH_MEMORY
    WHERE MCH_ID = V_MCH_ID;
    
    INSERT INTO NMS_DB.TMP_MCH_COLLECT_POLICY(MCH_ID,MNG_YN) VALUES(N_MCH_ID,'N');
    INSERT INTO NMS_DB.TMP_MCH_IP_TABLE(IP_ADDR, IF_ID, MCH_ID)  
    SELECT MM.MCH_IP, MI.IF_ID, MI.MCH_ID 
    FROM TMP_MCH_IF_INFO MI
    INNER JOIN TMP_MCH_INFO MM ON MI.MCH_ID = MM.MCH_ID
    WHERE MI.MCH_ID = N_MCH_ID LIMIT 1;
    INSERT INTO NMS_DB.TMP_IF_COLLECT_POLICY(IF_ID, MCH_ID) 
    SELECT IF_ID, MCH_ID
    FROM TMP_MCH_IF_INFO
    WHERE MCH_ID = N_MCH_ID;
    
  END LOOP LOOP_JOB;
  
  CLOSE CUR_JOB;
  
END ;;
