CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_TEST_MANUAL_PERF_R_CRT`(
IN _CRT_DT VARCHAR(50), 
IN _ST_DT DATETIME,
IN _END_DT DATETIME
)
BEGIN

    DELETE FROM PERF_R_MCH WHERE REG_DT >= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_ST_DT, "%H:%i:%S")) AND REG_DT <= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_END_DT, "%H:%i:%S")) AND MCH_ID >= 2000;
    DELETE FROM PERF_R_CPU WHERE REG_DT >= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_ST_DT, "%H:%i:%S")) AND REG_DT <= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_END_DT, "%H:%i:%S")) AND MCH_ID >= 2000;
    DELETE FROM PERF_R_MEMORY WHERE REG_DT >= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_ST_DT, "%H:%i:%S")) AND REG_DT <= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_END_DT, "%H:%i:%S")) AND MCH_ID >= 2000;
    DELETE FROM PERF_R_IF WHERE REG_DT >= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_ST_DT, "%H:%i:%S")) AND REG_DT <= CONCAT(_CRT_DT, ' ', DATE_FORMAT(_END_DT, "%H:%i:%S")) AND IF_ID >= 10000;
    
    INSERT INTO NMC_DB.PERF_R_MCH(REG_DT, MCH_ID, RESP_TIME, PKT_LOSS_PC, TEMP)
    SELECT CONCAT(_CRT_DT, ' ', DATE_FORMAT(P.REG_DT, "%H:%i:%S")), M.MCH_ID, P.RESP_TIME, P.PKT_LOSS_PC, P.TEMP
    FROM TMP_MCH_INFO M
    INNER JOIN NMC_DB.PERF_R_MCH P ON P.MCH_ID = M.R_MCH_ID
    WHERE P.REG_DT >= _ST_DT AND P.REG_DT <= _END_DT;
    
    INSERT INTO NMC_DB.PERF_R_CPU(REG_DT, CPU_ID, MCH_ID, USE_PC)
    SELECT CONCAT(_CRT_DT, ' ', DATE_FORMAT(P.REG_DT, "%H:%i:%S")), M.CPU_ID, M.MCH_ID, P.USE_PC
    FROM MCH_CPU M
    LEFT OUTER JOIN TMP_MCH_CPU TM ON M.CPU_ID = TM.CPU_ID AND TM.CPU_ID > 0
    INNER JOIN NMC_DB.PERF_R_CPU P ON P.CPU_ID = TM.R_CPU_ID
    WHERE P.REG_DT >= _ST_DT AND P.REG_DT <= _END_DT;
    
    INSERT INTO NMC_DB.PERF_R_MEMORY(REG_DT, MEMORY_ID, MCH_ID, USE_PC, USE_DC, TOTAL_DC) 
    SELECT CONCAT(_CRT_DT, ' ', DATE_FORMAT(P.REG_DT, "%H:%i:%S")), M.MEMORY_ID, M.MCH_ID, P.USE_PC, P.USE_DC, P.TOTAL_DC
    FROM MCH_MEMORY M
    LEFT OUTER JOIN TMP_MCH_MEMORY TM ON M.MEMORY_ID = TM.MEMORY_ID AND TM.MEMORY_ID > 0
    INNER JOIN NMC_DB.PERF_R_MEMORY P ON P.MEMORY_ID = TM.R_MEMORY_ID
    WHERE P.REG_DT >= _ST_DT AND P.REG_DT <= _END_DT;
    
    INSERT INTO NMC_DB.PERF_R_IF(REG_DT, IF_ID, MCH_ID, BPS_IN_PC, BPS_IN_DC, BPS_OUT_PC, BPS_OUT_DC, PPS_IN_DC, PPS_OUT_DC, DISCARD_IN_DC, DISCARD_OUT_DC, ERROR_IN_DC, ERROR_OUT_DC, COLLISION_DC, CRC_DC, U_PPS_IN_DC, U_PPS_OUT_DC, M_PPS_IN_DC, M_PPS_OUT_DC, B_PPS_IN_DC, B_PPS_OUT_DC) 
    SELECT CONCAT(_CRT_DT, ' ', DATE_FORMAT(P.REG_DT, "%H:%i:%S")), 
    M.IF_ID, M.MCH_ID, 
    P.BPS_IN_PC, P.BPS_IN_DC, P.BPS_OUT_PC, P.BPS_OUT_DC, 
    P.PPS_IN_DC, P.PPS_OUT_DC, P.DISCARD_IN_DC, P.DISCARD_OUT_DC, 
    P.ERROR_IN_DC, P.ERROR_OUT_DC, P.COLLISION_DC, P.CRC_DC, 
    P.U_PPS_IN_DC, P.U_PPS_OUT_DC, P.M_PPS_IN_DC, P.M_PPS_OUT_DC, P.B_PPS_IN_DC, P.B_PPS_OUT_DC
    FROM MCH_IF_INFO M
    LEFT OUTER JOIN TMP_MCH_IF_INFO TM ON M.IF_ID = TM.IF_ID AND TM.IF_ID > 0
    INNER JOIN NMC_DB.PERF_R_IF P ON P.IF_ID = TM.R_IF_ID
    WHERE P.REG_DT >= _ST_DT AND P.REG_DT <= _END_DT;
      
