CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_TEST_MANUAL_PERF_R_CRT_SCH`(
IN _RUN_DT DATETIME
)
BEGIN

  SET @ST_DATE = _RUN_DT;
  SET @END_DATE = @ST_DATE;
    
  SET @SEC = SECOND(@ST_DATE);
  SET @ST_DATE = DATE_ADD(@ST_DATE, INTERVAL -@SEC SECOND);
  SET @MIN = MINUTE(@ST_DATE);
  SET @MIN_DEL = @MIN % 5;
  SET @ST_DATE = DATE_ADD(@ST_DATE, INTERVAL -@MIN_DEL MINUTE); 

  
  CALL SP_TEST_MANUAL_PERF_R_CRT(DATE_FORMAT(@ST_DATE, "%Y-%m-%d"), @ST_DATE,@END_DATE);
  
  UPDATE NMC_DB.PERF_L_MCH LP 
    JOIN NMS_DB.MCH_COLLECT_POLICY CP ON CP.MCH_ID = LP.MCH_ID 
    JOIN NMC_DB.PERF_R_MCH RM ON CP.MCH_ID = RM.MCH_ID AND RM.REG_DT = @ST_DATE
    LEFT OUTER JOIN NMS_DB.MCH_CPU C ON C.MCH_ID = CP.MCH_ID AND C.CPU_GB = 'T' 
    LEFT OUTER JOIN NMS_DB.MCH_MEMORY MM ON MM.MCH_ID = CP.MCH_ID AND MM.MEMORY_GB = 'M' 
    LEFT OUTER JOIN NMC_DB.PERF_R_CPU RC ON C.CPU_ID = RC.CPU_ID AND RC.REG_DT = @ST_DATE
    LEFT OUTER JOIN NMC_DB.PERF_R_MEMORY MR ON MR.MEMORY_ID = MM.MEMORY_ID AND MR.REG_DT = @ST_DATE
    SET LP.UPD_DT=@ST_DATE, LP.RESP_TIME=RM.RESP_TIME, LP.PKT_LOSS_PC=RM.PKT_LOSS_PC, LP.TEMP=RM.TEMP 
      , LP.CPU_USE_PC=RC.USE_PC, LP.MEMORY_USE_PC=MR.USE_PC, LP.MEMORY_USE_DC=MR.USE_DC, LP.MEMORY_TOTAL_DC=MR.TOTAL_DC 
    WHERE LP.UPD_DT<@ST_DATE AND CP.MCH_ID>2000; 
    
    UPDATE NMC_DB.PERF_L_IF LP 
    JOIN NMS_DB.IF_COLLECT_POLICY IP ON IP.IF_ID = LP.IF_ID 
    JOIN NMC_DB.PERF_R_IF RP ON RP.IF_ID = IP.IF_ID AND RP.REG_DT =@ST_DATE
    SET LP.UPD_DT=RP.REG_DT
        , LP.BPS_IN_PC=RP.BPS_IN_PC, LP.BPS_IN_DC=RP.BPS_IN_DC
        , LP.BPS_OUT_PC=RP.BPS_OUT_PC, LP.BPS_OUT_DC=RP.BPS_OUT_DC
        , LP.PPS_IN_DC=RP.PPS_IN_DC, LP.PPS_OUT_DC=RP.PPS_OUT_DC
        , LP.DISCARD_IN_DC=RP.DISCARD_IN_DC, LP.DISCARD_OUT_DC=RP.DISCARD_OUT_DC
        , LP.ERROR_IN_DC=RP.ERROR_IN_DC, LP.ERROR_OUT_DC=RP.ERROR_OUT_DC
        , LP.COLLISION_DC=RP.COLLISION_DC, LP.CRC_DC=RP.CRC_DC
        , LP.U_PPS_IN_DC=RP.U_PPS_IN_DC, LP.U_PPS_OUT_DC=RP.U_PPS_OUT_DC
        , LP.M_PPS_IN_DC=RP.M_PPS_IN_DC, LP.M_PPS_OUT_DC=RP.M_PPS_OUT_DC
        , LP.B_PPS_IN_DC=RP.B_PPS_IN_DC, LP.B_PPS_OUT_DC=RP.B_PPS_OUT_DC
    WHERE LP.UPD_DT<@ST_DATE AND IP.MCH_ID>2000; 
    
END ;;
